mapper.select:
select
from act_family_volunteer v ,act_project p left join portal_zcpa.province zp on zp.code=p.areaCode where v.projectId=p.id and v.volunteerId = #{volunteerId,jdbcType=VARCHAR and p.projectState=#{projectState,jdbcType=VARCHAR} union select
from act_project_volunteer pv ,act_project p left join portal_zcpa.province zp on zp.code=p.areaCode where pv.projectId=p.id and pv.volunteerId = #{volunteerId,jdbcType=VARCHAR} and p.projectState=#{projectState,jdbcType=VARCHAR}

select DISTINCT p.id pid,p.category,  v.areaCode
  from act_project p
  LEFT JOIN act_project_volunteer v on p.id=v.projectId
  where p.projectState='2' and p.id=#{projectId,jdbcType=VARCHAR}  and v.volunteerId =#{volunteerId,jdbcType=VARCHAR}
  and p.category=#{category,jdbcType=VARCHAR}
