mapper.select:
select ,
left join base_volunteer bv on bv.id = t.volunteerId
and bv.name is not null
ORDER BY ${orderBy}
ORDER BY t.createDate desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

    select count(1)
from act_project_volunteer
where projectId = #{projectId,jdbcType=VARCHAR} and volunteerId = #{volunteerId,jdbcType=VARCHAR}

mapper.delete:
delete from act_project_volunteer
where id in

    #{ids}

delete from act_project_volunteer
where targetId in

    #{ids}

mapper.select:
select t.id, t.areaCode, t.projectId, t.volunteerId,t.targetId, p.name,p.idCard,p.mobile,p.userType FROM act_project_volunteer t
left join act_project_target pt on pt.id=t.targetId
left join base_volunteer p on t.volunteerId = p.id where t.projectId = #{projectId,jdbcType=VARCHAR}
and t.targetId = #{targetId,jdbcType=VARCHAR}
