sql.where:
1 = 1

    and t.orgId = #{condition.orgId,jdbcType=VARCHAR}


    and t.orgName like  concat('%',#{condition.orgName,jdbcType=VARCHAR},'%')


    and t.contactPersonName = #{condition.contactPersonName,jdbcType=VARCHAR}


    and t.contactPersonTel = #{condition.contactPersonTel,jdbcType=VARCHAR}


    and t.type = #{condition.type,jdbcType=VARCHAR}


    and t.status = #{condition.status,jdbcType=VARCHAR}

mapper.select:
select ,o.orgName,o.areaCode orgAreaCode, o.orgAddress, o.content orgContent,m.`name`mName,m.content mContent,m.speciality, (t.quota-(SELECT count(*) from serve_finance_order o where o.financeId='2' and type='3') )as surplusQuota, case p.type when '4' then '常德市' when '3' then CONCAT('常德市',p.name) when '2' then CONCAT(ppp.ppNames,pp.pName,p.name) when '1' then CONCAT('常德市', ppp.ppNames,pp.pName,p.name) end areaName
left join base_organization o on o.id=t.orgId left join base_organization_member m on m.id=t.contactId left join portal_zcpa.province p on p.code=o.areaCode left join ( select name as pName, p.`code` as pCode, parent_code as ppCode from portal_zcpa.province p )pp on pp.pCode = p.parent_code left join (select name as ppNames, p.`code` as pppcode from portal_zcpa.province p )ppp on ppp.pppcode = pp.ppCode
ORDER BY ${orderBy}
ORDER BY t.createDate desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

    select count(1)
from serve_finance
where id = #{id,jdbcType=VARCHAR}

mapper.delete:
delete from serve_finance
where id in

    #{ids}

mapper.update:
update serve_finance
set fmUrl = #{coverUrl,jdbcType=VARCHAR}
