sql.where:
1 = 1

    and t.orderNo like concat('%',#{condition.orderNo,jdbcType=VARCHAR},'%')


    and t.type in

        #{item}



    and t.content = #{condition.content,jdbcType=VARCHAR}


    and t.orgPersonTel = #{condition.orgPersonTel,jdbcType=VARCHAR}


    and t.contactId = #{condition.contactId,jdbcType=VARCHAR}


    and t.createUserId = #{condition.createUserId,jdbcType=VARCHAR}

.--
    and t.createDate   >=  #{condition.startTime,jdbcType=TIMESTAMP}


    and t.createDate   <=  #{condition.endTime,jdbcType=TIMESTAMP}


    and t.status = #{condition.status,jdbcType=VARCHAR}

mapper.select:
select ,o.orgName,o.fmUrl,co.companyName,f.itemName,f.price,m.id contactId,m.name mName,m.imagePhoto,u.name userName,u.mobile userMobile
left join base_organization o on o.id=t.orgId left join base_company co on co.id=t.companyId left join serve_finance_item f on f.id=t.itemId left join base_organization_member m on m.id=t.contactId left join portal_hqgj.users u on u.user_id=t.createUserId
ORDER BY t.${orderBy}
ORDER BY t.createDate desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

    select count(1)
from serve_finance_order
where id = #{id,jdbcType=VARCHAR}

mapper.delete:
delete from serve_finance_order
where id in

    #{ids}

mapper.update:
update serve_finance_order
set status = #{status,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}
