mapper.insert:
insert into act_activity_report (id, activityId, postingId, 
  userId, reportedUserId, reportContemt, 
  remark, status, createDate
  )
values (#{id,jdbcType=VARCHAR}, #{activityId,jdbcType=VARCHAR}, #{postingId,jdbcType=VARCHAR}, 
  #{userId,jdbcType=VARCHAR}, #{reportedUserId,jdbcType=VARCHAR}, #{reportContemt,jdbcType=VARCHAR}, 
  #{remark,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}
  )

sql.where:
1 = 1

    and t.activityId = #{activityId,jdbcType=VARCHAR}


    and t.postingId = #{postingId,jdbcType=VARCHAR}


    and t.userId = #{userId,jdbcType=VARCHAR}


    and t.reportedUserId = #{reportedUserId,jdbcType=VARCHAR}


    and t.reportContemt = #{reportContemt,jdbcType=VARCHAR}


    and t.remark = #{remark,jdbcType=VARCHAR}


    and t.status = #{status,jdbcType=VARCHAR}


    and t.createDate = #{createDate,jdbcType=TIMESTAMP}

mapper.select:
select





        ORDER BY ${orderBy}


        ORDER BY t.createDate desc

    select count(1)
from act_activity_report
where  postingId=#{postingId,jdbcType=VARCHAR} and   userId=#{userId,jdbcType=VARCHAR}

