mapper.select:
select count(1)
from act_project_topic
where projectId = #{projectId,jdbcType=VARCHAR}

select count(1) as volunteerCount from act_project_volunteer v
  left join base_volunteer bv on bv.id = v.volunteerId
 where v.projectId=#{projectId,jdbcType=VARCHAR}
 and v.areaCode like concat(#{areaCode,jdbcType=VARCHAR},'%') and bv.name is not null

  and v.targetId=#{targetId,jdbcType=VARCHAR}

select bv.id,bv.name,bv.mobile,bv.areaCode,bv.userType, case p.type when '4' then '常德市' when '3' then concat_ws(">",'常德市',p.name) when '2' then concat_ws(">", ppp.ppNames,pp.pName,p.name) when '1' then concat_ws(">",'常德市', ppp.ppNames,pp.pName,p.name) end areaName from act_project_volunteer v left join base_volunteer bv on bv.id = v.volunteerId left join portal_zcpa.province p on p.code=bv.areaCode left join ( select name as pName, p.`code` as pCode, parent_code as ppCode from portal_zcpa.province p )pp on pp.pCode = p.parent_code left join (select name as ppNames, p.`code` as pppcode from portal_zcpa.province p )ppp on ppp.pppcode = pp.ppCode where v.projectId=#{projectId,jdbcType=VARCHAR} and v.areaCode like concat(#{areaCode,jdbcType=VARCHAR},'%') and bv.name is not null
and v.targetId=#{targetId,jdbcType=VARCHAR}
ORDER BY ${orderBy}
ORDER BY v.createDate desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}
