mapper.select:
select t.user_id userId, t.account, t.password, t.sex, t.idCard, t.name, t.corpId, t.areaCode, t.birthday, t.status, t.userType, t.lastLoginTime, t.mobile, t.email, t.createTime, t.curSyid, t.locked, t.lockedTime,a.orgName corpName,b.name areaName,t.deptId,c.name deptName from users t left join hqgj.base_organization a on t.corpId=a.id left join province b on t.areaCode=b.code
left join dept c on t.deptId=c.id
where t.user_id = #{id,jdbcType=VARCHAR}

mapper.delete:
