where user_id in

    #{id}

mapper.sql:
from users t left join hqgj.base_organization a on t.corpId=a.id left join province b on a.areaCode=b.code left join dept c on t.deptId=c.id where 0=0
and t.areaCode like concat(#{condition.areaCode,jdbcType=VARCHAR},'%')
and (t.name like concat('%',#{condition.name,jdbcType=VARCHAR},'%') or t.account like concat('%',#{condition.name,jdbcType=VARCHAR},'%'))
and t.status = #{condition.status,jdbcType=CHAR}
and t.corpId =#{condition.corpId,jdbcType=CHAR}

mapper.select:
select t.user_id userId, t.account, t.password, t.sex, t.idCard, t.name, t.corpId, t.deptId, t.areaCode, t.birthday, t.status, t.userType, t.lastLoginTime, t.mobile, t.email, t.createTime, t.curSyid, t.locked, t.lockedTime,a.orgName corpName,b.name areaName,c.name deptName
ORDER BY t.${orderBy}
ORDER BY t.createTime desc
limit #{start,jdbcType=DECIMAL}, #{limit,jdbcType=DECIMAL}

mapper.update:
update users
set password = #{password,jdbcType=CHAR}
where user_id = #{userId,jdbcType=VARCHAR}

update users
set status = #{status}
where user_id = #{userId,jdbcType=VARCHAR}

